import { useDencrypt } from "use-dencrypt-effect";

// const values = ["useDencrypt", "Customizable", "React Hook", "Text Effect"];
const values = ["Featured Post"];

const TextEffect = () => {
  const { result, dencrypt } = useDencrypt();

  React.useEffect(() => {
    let i = 0;

    const action = setInterval(() => {
      dencrypt(values[i]);

      i = i === values.length - 1 ? 0 : i + 1;
    }, 1000);

    return () => clearInterval(action);
  }, []);

  return (
    <div>
      {result}
      <style jsx>{`
        div {
          color: white;
          font-size: 32px;
          color: white;
          font-family: "BlenderProMedium", sans-serif;
          display: inline-flex;
          margin-left: 14%;
          margin-top: 10%;
          margin-bottom: 0%;
        }
      `}</style>
    </div>
  );
};

export default TextEffect;
